INSERT INTO `v_posts` VALUES (7830, 1, '2018-03-30 21:51:23', '2018-03-30 14:51:23', '<div id=\"collapseThree8\" class=\"panel-collapse collapse in\" aria-expanded=\"true\">\r\n<h4 class=\"panel-body\">Khoản vay tín chấp tại <a title=\"vay cấp tốc\" href=\"https://vaycaptoc.com\">vaycaptoc.com</a> không tự động gia hạn. Nhưng đội ngũ nhân viên của chúng tôi sẽ liên hệ lại bạn nếu bạn vẫn có nhu cầu sẽ được hỗ trợ gia hạn ( đáo hạn ) khoản vay mới cho bạn với lãi suất ưu đãi hơn rất nhiều. Khi đó bạn chỉ cần đem theo chứng minh nhân dân đến phòng giao dịch để được nhận tiền. Nếu trong quá trình thanh toán của khoản vay trước đó bạn thanh toán rất tốt thì sẽ được duyệt một hạn mức cao hơn.</h4>\r\nCảm ơn bạn đã quan tâm đến trang của chúng tôi.\r\nChúc bạn một ngày tốt lành.\r\n\r\n</div>', 'Khoản vay tín chấp của tôi có tự động gia hạn không (tiếp tục một kỳ hạn mới khi đến hạn) ?', '', 'inherit', 'closed', 'closed', '', '7776-revision-v1', '', '', '2018-03-30 21:51:23', '2018-03-30 14:51:23', '', 7776, 'https://vaycaptoc.com/7776-revision-v1/', 0, 'revision', '', 0);
